commits.content:
.error-card.mat-card {
    background: linear-gradient(-180deg, rgba(196, 196, 196, 0) 0%, $danger 100%);
    padding-bottom: 0;
    transition: all 0.5s ease-in-out;
    padding-bottom: 45px;
    box-sizing: border-box;
    height: 100%;
    // margin-bottom: 20px;
    .img-container {
      position: relative;
      height: 200px;
      // width: 100%;
      background-position: center center;
      background-repeat: no-repeat;
      background-size: cover;
      /* margin: 0; */
      // margin-left: 0;
      // margin-right: 0;
      .completion-rate {
        position: absolute;
        bottom: 10px;
        right: 10px;
      }
    }
    border-radius: $border-radius-xs;
    overflow: hidden;
    .card-title {
      margin-bottom: 8px;
    }
    .card-subtitle {
      color: $primary;
      align-items: flex-start;
      .icon, .mat-icon {
        width: 14px;
        vertical-align: text-bottom;
        flex: 0 0 18px;
        margin-inline-end: 5px;
      }
      .icon {
        svg {
          width: 1rem;
          height: 1.3rem;
          fill: $primary;
        }
      }
    }
    .mat-card-actions {
      padding-bottom: 0 !important;
      width: calc(100% + 32px);
      margin: 0 -16px 0 -16px !important;
      position: absolute;
      bottom: 0;
      width: 100%;
    
      button {
        border-radius: 0 !important;
        width: 100%;
        height: 100%;
  
      }
      &:last-child {
        margin-bottom: 0 ;
      }
    }
    .card-address {
      .mat-icon {
        margin-right: 7px;
        width: 18px;
      }
    }
    &:hover {
      box-shadow: 0px 6px 12px -6px rgba(var(--color-dark-shade-rgb), 0.12), 0px 8px 24px -4px rgba(var(--color-dark-shade-rgb), 0.08);
      filter: drop-shadow(0px 2px 10px rgba(0, 100, 175, 0.53));
  
    }
  }
  .mat-card-strech-section {
    width: calc(100% + 32px);
    margin: 0 -16px 16px -16px;
  }
